input = ""
sample_input = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"
# part one
def detect_marker(sequence: str) -> int:
    for i, char in enumerate(sequence):
        if len(set([char, sequence[i + 1], sequence[i + 2], sequence[i + 3]])) == 4:
            return i + 4
    return None


print(detect_marker(input))

# part two
def detect_message(sequence: str) -> int:
    for i, char in enumerate(sequence):
        if len(set([char] + [sequence[i + n] for n in range(15)])) == 14:
            return i + 15
    return None


print(detect_message(input))
